import React, { useEffect, useState } from "react";
import {NavLink} from "react-router-dom";

const Navbar=()=>{

    const [theme,setTheme]=useState("lighttheme");

  const Toggletheme=()=>{
    theme==="darktheme" ? setTheme("lighttheme") : setTheme("darktheme");
  };

  useEffect(()=>{
document.body.className=theme;
  },[theme]);

    return(
        <>
        <div className="navbarr " >
    <div className="nav-1">
        <h1><span className="firstname">Rishav</span> <strong className="caste">Sachdeva</strong></h1>
    </div>
    <div>
        <ul  className="nav-2">
        <NavLink className="nav-elements"   exact to="/">Home</NavLink>
            <NavLink className="nav-elements"  exact to="/about">About Me</NavLink>
            <NavLink className="nav-elements" exact to='/certificates'>Certifications</NavLink>
            <NavLink className="nav-elements" exact to='/skills'>Skills</NavLink>
            <button className="dark-btn" onClick={Toggletheme}>Dark-mode</button>
        </ul>
    </div>
</div>
        
        </>
    );
}
export default Navbar;
